table.trace-table {
    color: #333;
    font-family: Helvetica, Arial, sans-serif;
    width: 100%;
    border-collapse: collapse;
    border-spacing: 0;
    overflow-x: auto;
    overflow-y: auto;
}
table.trace-table tr td,
table.trace-table tr th {
    border: 1px solid transparent;
    /* No more visible border */
    height: 30px;
    transition: all 0.3s;
    /* Simple transition for hover effect */
}
table.trace-table tr th {
    background: #DFDFDF;
    /* Darken header a bit */
    font-weight: bold;
}
table.trace-table tr td {
    background: #FAFAFA;
    text-align: center;
}

table.trace-table tr:nth-child(even) td {
    background: gray;
}

tr:nth-child(odd) table.trace-table td {
    background: blue;
}
table.trace-table tr td:hover {
    background: red;
    color: yellow;
}

